INSERT INTO `wp_posts` VALUES (505,1,'2020-11-05 22:26:34','2020-11-05 22:26:34','<!-- wp:paragraph -->\n<p>A creative solution to the 2014 economic crisis, generating over 70% of growth for the agency Buutvrij for Life and gained over a €300.000 of free media publicity through social media and press attention.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Agency: Buutvrij for Life<br>Role: Junior Art Director</p>\n<!-- /wp:paragraph -->','Buutvrij for Life <br> 40 days fast','','inherit','closed','closed','','429-revision-v1','','','2020-11-05 22:26:34','2020-11-05 22:26:34','',429,'http://tomderegt.local/uncategorized/429-revision-v1/',0,'revision','',0);
